package com.beaver.jan2211.main;

public class User {
	private int r=1;
	private int c=2;
	private int p=3;
	
	public User() {
		// TODO Auto-generated constructor stub
	}

	public User(int r, int c, int p) {
		super();
		this.r = r;
		this.c = c;
		this.p = p;
	}

	public int getR() {
		return r;
	}

	public void setR(int r) {
		this.r = r;
	}

	public int getC() {
		return c;
	}

	public void setC(int c) {
		this.c = c;
	}

	public int getP() {
		return p;
	}

	public void setP(int p) {
		this.p = p;
	}
	
	
}
